var max = require('./restrictions/maximize');
var min = require('./restrictions/minimize');
var result;

function Calculate(minmax, objective, restrictions, callback) {
    if (minmax == "max") {
        result = JSON.stringify(max.maximize(objective, restrictions));
    }else if(minmax == "min"){
        result = JSON.stringify(min.minimize(objective, restrictions));
    }

    // console.log(JSON.parse(result).length);
    // console.log(max.maximize(objective, restrictions)[0].restriction0[2].value);

    return process.nextTick(function() {
        callback(result);
    });
}

module.exports = {Calculate}
